
pub fn puzzle1() -> i32 {
  let data = String::from("



");

  let mut button : i32 = 5;

  let mut answer : i32 = 0;

  for line in data.split("\n") {
    for char in line.chars() {
      button = move_button(button, char);
    }
    answer = (answer * 10) + button;
  }
  answer
}

pub fn puzzle2() -> String {
let data = String::from("



");

  let mut button : i32 = 5;

  let mut answer = String::from("");

  for line in data.split("\n") {
    for char in line.chars() {
      button = move_button_fancy(button, char);
    }
    answer.push_str(&format!("{:X}", button));
  }
  answer
}

fn move_button(button: i32, ch: char) -> i32 {
  match ch {
    'U' => {
      if button > 3 { button - 3 } else { button }
    },
    'D' => {
      if button < 7 { button + 3 } else { button }
    },
    'L' => {
      match button {
        1|4|7 => button,
        i => i - 1,
      }
    },
    'R' => {
      match button {
      3|6|9 => button,
      i => i + 1,
      }
    },
    _ => panic!("Invalid command: {}", ch)
  }
}

fn move_button_fancy(button: i32, ch: char) -> i32 {
  match ch {
    'U' => {
      match button {
        1|2|4|5|9 => button,
        3 => 1,
        6 => 2,
        7 => 3,
        8 => 4,
        10 => 6,
        11 => 7,
        12 => 8,
        13 => 11,
        _ => panic!("Bad value for button.")
      }
    },
    'D' => {
      match button {
        5|9|10|12|13 => button,
        1 => 3,
        2 => 6,
        3 => 7,
        4 => 8,
        6 => 10,
        7 => 11,
        8 => 12,
        11 => 13,
        _ => panic!("Bad value for button."),
      }
    },
    'L' => {
      match button {
        1|2|5|10|13 => button,
        i => i - 1,
      }
    },
    'R' => {
      match button {
      1|4|9|12|13 => button,
      i => i + 1,
      }
    },
    _ => panic!("Invalid command: {}", ch)
  }
}
